import { CgHeart } from 'react-icons/cg';
import styled from '@emotion/styled';
export const HeartBtn = styled(CgHeart)`
  color: var(--modal-close-icon);
  transition: all 250ms cubic-bezier(0.4, 0, 0.2, 1);
  cursor: pointer;

  :hover {
    color: var(--accent);
  }

  @media (min-width: 767px) {
    top: 18px;
    right: 18px;
  }
`;
