import { useDispatch, useSelector } from 'react-redux';
import {
  selectCars,
  selectFavorites,
  selectFilter,
} from '../../redux/selectors';
import {
  addFavorite,
  deleteFavorite,
} from '../../redux/favorites/favoritesSlice';
import FilterForm from '../FilterForm/FilterForm';
import { useEffect, useState } from 'react';
import AddModalCars from 'components/AddModalCars/AddModalCars';

const CarsList = ({ handleCloseModal, handleOpenModal, isModalOpen }) => {
  const toggleFavorite = id => {
    favoriteCars.includes(id)
      ? dispatch(deleteFavorite(id))
      : dispatch(addFavorite(id));
  };
  return (
    <>
      <div>
        {filteredCars.length === 0 ? (
          <p>No cars</p>
        ) : (
          <ul>
            {filteredCars.map(car => {
              return (
                <li key={car.id}>
                  <div>
                    <span>
                      {car.make} : {car.model}
                    </span>
                    <button onClick={handleOpenModal}>Learn More</button>
                  </div>
                  <button onClick={() => toggleFavorite(car.id)}>
                    {favoriteCars.includes(car.id) ? '-' : '+'}
                  </button>
                </li>
              );
            })}
          </ul>
        )}
      </div>
      {isModalOpen && <AddModalCars onClose={handleCloseModal} />}
    </>
  );
};
export default CarsList;
