import { useEffect, useState } from "react";

const FilterForm = ({ cars }) => {
  const [selectedValue, setSelectedValue] = useState("");
  const [uniqueCars, setUniqueCars] = useState([]);
  useEffect(() => {
    const uniqueCarsData = uniqueCarsWithCount(cars, "make");
    setUniqueCars(uniqueCarsData);
  }, [cars]);

  const handleSelectChange = (event) => {
    setSelectedValue(event.target.value);
  };
  const uniqueCarsWithCount = (arr, field) => {
    const uniqueValues = {};
    arr.forEach((item) => {
      const value = item[field];
      if (uniqueValues.hasOwnProperty(value)) {
        uniqueValues[value]++;
      } else {
        uniqueValues[value] = 1;
      }
    });

    return arr.map((item) => ({
      ...item,
      count: uniqueValues[item[field]], // Додаємо лічильник до об'єкту
    }));
  };

  return (
    <div>
      <form style={{ display: "flex" }}>
        <div>
          <label htmlFor="dropdown">Car brand</label>
          <select
            id="dropdown"
            value={selectedValue}
            onChange={handleSelectChange}
          >
            <option value="">Оберіть модель</option>
            {uniqueCars.map((cars) => (
              <option key={cars.make} value={cars.make}>
                {cars.make}
              </option>
            ))}
          </select>
        </div>
      </form>
    </div>
  );
};
export default FilterForm;
