import { useDispatch, useSelector } from 'react-redux';
import { addFavorite, deleteFavorite } from 'redux/favorites/favoritesSlice';
import { selectFavorites } from 'redux/selectors';
import {
  ButtonMore,
  FillHeartBtn,
  HeartBtn,
  Img,
  SpanLabel,
  SpanTitle,
  WrapperLabel,
  WrapperMakeModel,
  WrapperText,
  WrapperTitle,
} from './CardCar.styled';

const CardCar = ({ key, car, handleOpenModal }) => {
  const dispatch = useDispatch();
  const favoriteCars = useSelector(selectFavorites);
  const toggleFavorite = id => {
    favoriteCars.includes(id)
      ? dispatch(deleteFavorite(id))
      : dispatch(addFavorite(id));
  };
  return (
    <>
      <li
        key={key}
        style={{
          width: '274px',
          height: '426px',
          backgroundColor: '#fff',
          position: 'relative',
        }}
      >
        {favoriteCars.includes(car.id) ? (
          <FillHeartBtn onClick={() => toggleFavorite(car.id)} />
        ) : (
          <HeartBtn onClick={() => toggleFavorite(car.id)} />
        )}

        <Img src={car.img} alt={`${car.make} ${car.model}`} />
        <WrapperText>
          <WrapperTitle>
            <SpanTitle>
              {car.make} <SpanTitle model={true}>{car.model}</SpanTitle>
              {', '}
              {car.year}
            </SpanTitle>

            <SpanTitle>{car.rentalPrice}</SpanTitle>
          </WrapperTitle>
          <WrapperLabel>
            <Stroke>
              <SpanLabel>
                {car.address.split(', ')[car.address.split(', ').length - 2]}
              </SpanLabel>
              <SpanLabel>
                {car.address.split(', ')[car.address.split(', ').length - 1]}
              </SpanLabel>
              <SpanLabel>{car.rentalCompany}</SpanLabel>
            </Stroke>
            <SpanLabel>{car.type}</SpanLabel>
          </WrapperLabel>
          <ButtonMore onClick={handleOpenModal}>Learn More</ButtonMore>
        </WrapperText>
      </li>
    </>
  );
};
export default CardCar;
