import { useDispatch, useSelector } from 'react-redux';
import { addFavorite, deleteFavorite } from 'redux/favorites/favoritesSlice';
import { selectFavorites } from 'redux/selectors';
import { HeartBtn } from './CardCar.styled';

const CardCar = ({ key, car, handleOpenModal }) => {
  const dispatch = useDispatch();
  const favoriteCars = useSelector(selectFavorites);
  const toggleFavorite = id => {
    favoriteCars.includes(id)
      ? dispatch(deleteFavorite(id))
      : dispatch(addFavorite(id));
  };
  return (
    <>
      <li
        key={key}
        style={{
          width: '274px',
          height: '426px',
          backgroundColor: '#fff',
          position: 'relative',
        }}
      >
        <HeartBtn fill="blue" onClick={() => toggleFavorite(car.id)} />

        <img
          src={car.img}
          alt={car.make}
          style={{
            width: '274px',
            height: '268px',
            borderRadius: '14px',
          }}
        />
        <div>
          <span>
            {car.make} : {car.model}
          </span>
          <button onClick={handleOpenModal}>Learn More</button>
        </div>
      </li>
    </>
  );
};
export default CardCar;
