import styled from '@emotion/styled';
export const Input = styled.input`
  font-family: Manrope;
  font-size: 18px;
  font-weight: 500;
  line-height: 20px;
  letter-spacing: 0em;
  text-align: left;
  color: #121417;
  padding: 14px 18px;
  border: none;
  background-color: rgba(247, 247, 251, 1);
  width: 160px;
  outline: none;
  :first-of-type {
    border-top-left-radius: 14px;
    border-bottom-left-radius: 14px;
    border-right: 1px solid #8a8a89;
    :before {
      content: 'From:';
      position: absolute;
      color: black;
    }
  }
  :last-of-type {
    border-top-right-radius: 14px;
    border-bottom-right-radius: 14px;
    :before {
      content: 'To:';
    }
  }
`;
